package com.example.user.popularmoviesapp.activities;import android.content.Intent;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.design.widget.CollapsingToolbarLayout;import android.support.v4.app.ShareCompat;import android.support.v7.app.AppCompatActivity;import android.support.v7.widget.DefaultItemAnimator;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.support.v7.widget.Toolbar;import android.util.Log;import android.view.Menu;import android.view.MenuItem;import android.widget.ImageView;import android.widget.RatingBar;import android.widget.TextView;import com.example.user.popularmoviesapp.Helper.Constants;import com.example.user.popularmoviesapp.R;import com.example.user.popularmoviesapp.adapters.ReviewsAdapter;import com.example.user.popularmoviesapp.adapters.TrailersAdapter;import com.example.user.popularmoviesapp.model.MovieReviewResults;import com.example.user.popularmoviesapp.model.MovieReviews;import com.example.user.popularmoviesapp.model.MovieVideoResults;import com.example.user.popularmoviesapp.model.MovieVideos;import com.example.user.popularmoviesapp.model.Movies;import com.example.user.popularmoviesapp.network.TmdbRestClient;import com.squareup.picasso.Picasso;import java.text.DecimalFormat;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;import retrofit2.Call;import retrofit2.Callback;import retrofit2.Response;public class DetailsActivity extends AppCompatActivity {    private final String BACKDROP_IMAGE_URL = "http://image.tmdb.org/t/p/w500";    private final String POSTER_IMAGE_URL = "http://image.tmdb.org/t/p/w185";    @BindView(R.id.details_collapsing_toolbar)    CollapsingToolbarLayout collapsingToolbarLayout;    @BindView(R.id.details_ct_back_drop)    ImageView backDropImage;    @BindView(R.id.details_cv_movie_image)    ImageView thumbnailImage;    @BindView(R.id.details_tv_title)    TextView detailsTitle;    @BindView(R.id.details_tv_release_date)    TextView detailsReleaseDate;    @BindView(R.id.details_tv_duration)    TextView detailsDuration;    @BindView(R.id.details_tv_ratings)    TextView detailsRating;    @BindView(R.id.details_tv_overview)    TextView detailsOverview;    @BindView(R.id.details_rv_videos)    RecyclerView trailersRecyclerView;    @BindView(R.id.reviews_recycler_view)    RecyclerView reviewsRecyclerView;    @BindView(R.id.details_rating_bar)    RatingBar ratingBar;    @BindView(R.id.reviews_empty_text)    TextView reviewsEmptyText;    private int movie_id;    Movies movie;    private TrailersAdapter trailersAdapter;    private List<MovieVideoResults> trailersList;    private static final String TAG = TrailersAdapter.class.getSimpleName();    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.details_activity);        ButterKnife.bind(this);        Toolbar toolbar  = (Toolbar) findViewById(R.id.details_toolbar);        setSupportActionBar(toolbar);        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(this,                LinearLayoutManager.HORIZONTAL,                false);        trailersRecyclerView.setLayoutManager(linearLayoutManager);        if(getIntent() != null)        {            if(getIntent().hasExtra(Constants.MOVIE_INTENT_ID))            {                    movie = getIntent().getParcelableExtra(Constants.MOVIE_INTENT_ID);                    movie_id = movie.getId();                Log.d(TAG,"The selected movie_id is " +movie_id);            }        }        loadMovieDetails();        loadMovieReviews();        loadMoviesTrailers();    }    /*Network Call to get the Movie Details*/    private void loadMovieDetails()    {        Call<Movies> moviesCall = TmdbRestClient.getInstance()                .getMovieDetailsResponse()                .getMovieDetails(movie_id);        moviesCall.enqueue(new Callback<Movies>() {            @Override            public void onResponse( Call<Movies> call,  Response<Movies> response) {                Movies moviesList = response.body();                collapsingToolbarLayout.setTitle(moviesList.getTitle());                Picasso.with(getApplicationContext())                        .load(BACKDROP_IMAGE_URL + moviesList.getBackdropPath())                        .error(R.drawable.no_movie_image)                        .placeholder(R.drawable.load)                        .into(backDropImage);                Picasso.with(getApplicationContext())                        .load(POSTER_IMAGE_URL + moviesList.getPosterPath())                        .error(R.drawable.no_movie_image)                        .placeholder(R.drawable.load)                        .into(thumbnailImage);                detailsTitle.setText(moviesList.getOriginalTitle());                detailsReleaseDate.setText(moviesList.getReleaseDate());                String runTime = Integer.toString(moviesList.getRuntime()) + " minutes ";                detailsDuration.setText(runTime);                detailsRating.setText(String.valueOf(moviesList.getVoteAverage()));                DecimalFormat decimalFormat = new DecimalFormat("#0.00");                Double voteAverage = moviesList.getVoteAverage();                ratingBar.setRating(Float.parseFloat(decimalFormat.format(voteAverage)));                detailsOverview.setText(moviesList.getOverview());            }            @Override            public void onFailure(@NonNull Call<Movies> call, @NonNull Throwable t) {                Log.e(TAG,t.getMessage());            }        });    }    /*Network call to get the Movie Trailers List*/    private void loadMoviesTrailers()    {        Call<MovieVideos> videosCall = TmdbRestClient.getInstance()                .getMovieDetailVideosResponse()                .getMovieVideos(movie_id);        videosCall.enqueue(new Callback<MovieVideos>() {            @Override            public void onResponse(@NonNull Call<MovieVideos> call, @NonNull Response<MovieVideos> response) {                trailersList = response.body().getResults();                trailersRecyclerView.setAdapter(new TrailersAdapter(trailersList,getApplicationContext()));                trailersRecyclerView.smoothScrollToPosition(0);                trailersRecyclerView.setItemAnimator(new DefaultItemAnimator());            }            @Override            public void onFailure(Call<MovieVideos> call, Throwable t) {                Log.e(TAG,t.getMessage());            }        });    }    /*Network call to get the Movie Reviews List*/    private void loadMovieReviews()    {       Call<MovieReviews> reviewsCall = TmdbRestClient.getInstance()               .getMovieDetailReviewsResponse()               .getMovieReviews(movie_id);        reviewsCall.enqueue(new Callback<MovieReviews>() {            @Override            public void onResponse(@NonNull Call<MovieReviews> call, @NonNull Response<MovieReviews> response) {                List<MovieReviewResults> reviews = response.body().getResults();                if(reviews.isEmpty())                {                    reviewsEmptyText.setText(getResources().getString(R.string.empty_reviews));                }else {                    reviewsRecyclerView.setLayoutManager(new LinearLayoutManager(getApplicationContext()));                    reviewsRecyclerView.setAdapter(new ReviewsAdapter(getApplicationContext(), reviews));                    reviewsRecyclerView.smoothScrollToPosition(0);                    reviewsRecyclerView.setItemAnimator(new DefaultItemAnimator());                }            }            @Override            public void onFailure(Call<MovieReviews> call, Throwable t) {                Log.d(TAG,t.getMessage());            }        });    }    /*Try using this in context with the toolbar.setMenuItem*/    @Override    public boolean onCreateOptionsMenu(Menu menu) {        getMenuInflater().inflate(R.menu.details,menu);        return true;    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        int id = item.getItemId();        if(id == android.R.id.home )        {            onBackPressed();            return true;        }        else if (id == R.id.menu_share)        {            ShareCompat.IntentBuilder intentBuilder = ShareCompat.IntentBuilder.from(this);            Intent sharingIntent = intentBuilder                    .setType("text/plain")                    .setText(Constants.YOUTUBE_URL_BROWSER + trailersList.get(0).getKey())                    .setChooserTitle(getResources().getString(R.string.share_title))                    .createChooserIntent();            if(sharingIntent.resolveActivity(getPackageManager()) != null )            {                startActivity(sharingIntent);            }        }        return super.onOptionsItemSelected(item);    }}